import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Todo = () => {
	const [todos, setTodos] = useState([]);
	const [input, setInput] = useState('');

	const handleNewTodoItem = async () => {
		setTodos([...todos, input]);
		try {
			await axios.post(
				'https://react-hooks-intro-940a4.firebaseio.com/todos.json',
				{ todo: input ``}
			);
		} catch (error) {
			console.log(error);
		}
	};

	useEffect(() => {
		setTimeout(() => {
			console.log(`FETCHED ${input} ON MOUNT`);
		}, 2500);
	}, [input, todos]);

	const handleInputChange = e => setInput(e.target.value);

	const handleAddButtonClick = () => handleNewTodoItem();

	const handleInputEnterPress = e =>
		e.key === 'Enter' ? handleNewTodoItem() : null;

	return (
		<>
			<input
				type="text"
				placeholder="Todo"
				value={input}
				onChange={e => handleInputChange(e)}
				onKeyPress={e => handleInputEnterPress(e)}
			/>
			<button type="button" onClick={handleAddButtonClick}>
				Add
			</button>
			<button onClick={() => setTodos([])}>Clear</button>
			<ul>
				{todos.map((todo, i) => (
					<li key={i}>{todo}</li>
				))}
			</ul>
		</>
	);
};
export default Todo;
